# I had trouble updating the data set with a primary key, so I just imported it and then ran
# ALTER TABLE northamerica ADD id int NOT NULL AUTO_INCREMENT primary key;

CREATE TABLE northamerica (
    `id` INT NOT NULL AUTO_INCREMENT,
    `record_id` INT,
    `continent` VARCHAR(2) CHARACTER SET utf8,
    `status` VARCHAR(10) CHARACTER SET utf8,
    `sporder` VARCHAR(15) CHARACTER SET utf8,
    `family` VARCHAR(16) CHARACTER SET utf8,
    `genus` VARCHAR(16) CHARACTER SET utf8,
    `species` VARCHAR(16) CHARACTER SET utf8,
    `log_mass_g` NUMERIC(10, 9),
    `comb_mass_g` NUMERIC(9, 1),
    `reference` VARCHAR(19) CHARACTER SET utf8,
    `primary key`(id)
);
